namespace App\Api\External\Response\Data;

use App\Api\External\Entity\User;

class LoginData
{
    /**
     * @var User
     */
    private $user;

    /**
     * LoginData constructor.
     */
    public function __construct(User $user)
    {
        $this->user = $user;
    }

    public function getUser(): User
    {
        return $this->user;
    }
}
